CREATE TABLE test4posttime_distance
(
  id serial NOT NULL,
  posttime_1 posttime,
  posttime_2 posttime,
  CONSTRAINT test4posttime_distance_pkey PRIMARY KEY (id)
)
WITH (
  OIDS=FALSE
);


SELECT tm_distance(posttime_1, posttime_2) FROM test4posttime_distance;

